import React from 'react';
import { useDispatch, useSelector } from 'react-redux';

// material-ui

import {
  makeStyles,
  Box,
  Divider,
  CircularProgress,
  Grid,
  Typography,
  Button,
} from '@material-ui/core';
import { Formik, Form } from 'formik';
import * as Yup from 'yup';
import Textfield from '../partials/FormUI/Textfield';
import { updateUser, updatePassword } from '../../store/actions/authActions';

import Snackbar from '../reusables/Snackbar';
import getCookie from '../../helpers/getCookie';

// style const
const useStyles = makeStyles((theme) => ({
  root: {
    borderRadius: '5px',
    background: 'white',
    minHeight: '90vh',
    padding: '40px 30px',
    overflow: 'hidden',

    '@media (max-width: 900px)': {
      padding: '20px',
    },
  },
  profileheading: {
    fontWeight: '700',
    fontSize: '1.4rem',
    fontFamily: 'mulish',
    marginBottom: '25px',
  },
  profileheading_sub: {
    fontWeight: '600',
    fontSize: '1rem',
    fontFamily: 'mulish',
    marginBottom: '13px',
  },
  button: {
    borderRadius: '5px',
    display: 'flex',
    justifyContent: 'flex-start',
    textTransform: 'capitalize',
    background: 'transparent',
    color: 'black',
    alignItems: 'left',
    width: '280px',

    '&:hover': {
      color: '#1275D1',
      background: '#E2ECF6',
    },
  },
  descriptive_text: {
    fontSize: '.9rem',
    color: 'gray',
  },
  personalinfo_submit: {
    width: '100px',
    marginTop: '15px',
  },
}));

export default function Mydetails() {
  const [alertContent, setAlertContent] = React.useState({
    type: 'error',
    content: '',
  });

  const [open, setOpen] = React.useState(false);
  const auth = useSelector((state) => state.authReducer);

  const handleClick = () => {
    setOpen(true);
  };

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };

  const dispatch = useDispatch();
  const {
    root,
    profileheading,
    profileheading_sub,
    descriptive_text,
    personalinfo_submit,
  } = useStyles();

