          </Typography>
          <Divider />

          <Formik
            initialValues={{
              oldPassword: '',
              password: '',
              confirmPassword: '',
            }}
            onSubmit={async (values) => {
              const cookie = getCookie(auth.token);

              if (!cookie) {
                await setAlertContent({
                  type: 'error',
                  content: 'session expired',
                });
                handleClick();
                await setTimeout(() => {
                  dispatch({ type: 'SIGN_OUT' });
                }, 3000);
                return;
              }

              await dispatch(
                updatePassword({
                  password: values.password,
                  oldPassword: values.oldPassword,
                })
              );
              if (window.store.getState().authReducer.updated === true) {
                await setAlertContent({
                  type: 'success',
                  content: 'You password has been successfully updated',
                });
                handleClick();
                dispatch({ type: 'ON_UPDATE_SUCCESS' });
              } else {
                await setAlertContent({
                  type: 'error',
                  content: window.store.getState().authReducer.error,
                });
                handleClick();
              }
            }}
            validationSchema={Yup.object().shape({
              oldPassword: Yup.string()
                .min(6, 'password must be atleast 6 characters')
                .required('Password is required'),
              password: Yup.string()
                .min(6, 'password must be atleast 6 characters')
                .required('Password is required'),
              confirmPassword: Yup.string()
                .oneOf([Yup.ref('password'), null], 'password must match')
                .required('Please confirm password ðŸ˜±'),
            })}
          >
            {({ isSubmitting }) => (
              <Form autoComplete="off">
                <Grid container>
                  <Grid
                    style={{
                      paddingRight: '20px',
                      paddingLeft: '0px',
                      paddingTop: '5px',
                    }}
                    xs={12}
                    sm={4}
                    item
                  >
                    <Typography className={descriptive_text}>
                      Assertively utilize adaptive customer service for future
                      proof platform. completely drive optimal markets
                    </Typography>
                  </Grid>
                  <Grid xs={12} sm={4} item>
                    <Box marginRight="10px" marginTop="10px">
                      <Textfield
                        type="password"
                        name="oldPassword"
                        helpertext="Current Password"
                      />
                    </Box>
                    <Box marginRight="10px" marginTop="10px">
                      <Textfield
                        type="password"
                        name="password"
                        helpertext="New Password"
                      />
                    </Box>
                    <Box marginRight="10px" marginTop="10px">
                      <Textfield
                        type="password"
                        name="confirmPassword"
                        helpertext="Confirm Password"
                      />
                    </Box>
                    <Box>
                      <Button
                        startIcon={
                          isSubmitting ? (
                            <CircularProgress color="primary" size="1rem" />
                          ) : null
                        }
                        color="secondary"
                        className={personalinfo_submit}
                        type="submit"
                        variant="contained"
                      >
                        save
                      </Button>
                    </Box>
                  </Grid>
                  <Grid xs={12} sm={4} item></Grid>
                </Grid>
              </Form>
            )}
          </Formik>
        </Box>
      </Box>
    </Box>
