import React from "react";
import { Typography, makeStyles } from "@material-ui/core";
import LocalShippingIcon from "@material-ui/icons/TwoWheeler";
import EcoIcon from "@material-ui/icons/Eco";
import BusinessIcon from "@material-ui/icons/Business";

const useStyles = makeStyles((theme) => ({
  leaderBoard_left_h1: {
    lineHeight: "40px",
    fontFamily: "Inter, sans-serif",
    fontSize: "1.8rem",
    fontWeight: "bold",
  },
  icon_box_h1: {
    fontFamily: "Inter, sans-serif",
    fontSize: "1rem",
    fontWeight: "bold",
  },
  why_choose_us: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    minHeight: "60vh",
    height: "max-content",
    overflow: "hidden",
    background: "url('bg.jpg')",
    backgroundRepeat: "no-repeat",
    objectFit: "contain",
    backgroundPosition: "calc(100vw - 170px) 160px",
    backgroundSize: "200px",
  },
  icon_boxes: {
    display: "flex",
    justifyContent: "center",
    padding: "30px 30px",
    flexWrap: "wrap",
  },
  icon_box: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    marginLeft: "15px",
    marginTop: "15px",
  },
  icon_box_icon: {
    fontSize: "50px",
    color: "#212121",
  },
  icon_box_p: {
    fontFamily: "Inter, sans-serif",
    textAlign: "center",
    marginTop: "2px",
  },
  wcu_service: {
    fontFamily: "Inter, sans-serif",
    padding: "0 20px",
    fontSize: ".9rem",
  },
}));

export default function WhyChooseUs() {
  const {
    why_choose_us,
    leaderBoard_left_h1,
    icon_boxes,
    icon_box_icon,
    icon_box,
    icon_box_h1,
    icon_box_p,
    wcu_service,
  } = useStyles();
  const Iconbox = ({ details1, details2, Icon, title }) => (
    <div className={icon_box}>
      {Icon}
      <Typography className={icon_box_h1} variant="h2" component="h1">
        {title}
      </Typography>
      <Typography className={icon_box_p} component="p">
        {details1} <br /> {details2}
      </Typography>
    </div>
  );

  return (
    <div className={why_choose_us}>
      <Typography className={leaderBoard_left_h1} variant="h2" component="h1">
        Why choose us?
      </Typography>
      <Typography className={wcu_service} component="p">
        Easy booking fair prices
      </Typography>

      <div className={icon_boxes}>
        <Iconbox
          Icon={<EcoIcon className={icon_box_icon} />}
          title="Skilled Worker"
          details1="We serve the best and experienced"
          details2="expert workers"
        />
        <Iconbox
          Icon={<BusinessIcon className={icon_box_icon} />}
          title="Best offer"
          details1="We give the best offer for our"
          details2="valuable customers"
        />
        <Iconbox
          Icon={<LocalShippingIcon className={icon_box_icon} />}
          title="Fast Arrival"
          details1="We Choose the"
          details2="Available helper"
        />
      </div>
    </div>
  );
}
