import React, { useState } from 'react';
import { Formik, Form } from 'formik';
import * as Yup from 'yup';
import { useNavigate } from 'react-router-dom';
import {
  Typography,
  makeStyles,
  Button,
  Grid,
  Box,
  CircularProgress,
} from '@material-ui/core';
import { useDispatch } from 'react-redux';
import { Redirect } from 'react-router-dom';

//local import
import Textfield from '../partials/FormUI/Textfield';
import { login } from '../../store/actions/authActions';
import axios from 'axios';

const useStyles = makeStyles((theme) => ({
  root_left_lower: {
    '& > :nth-child(1)': {
      fontFamily: 'Mulish',
      fontSize: '.9rem',
    },
    '& > :nth-child(2)': {
      fontFamily: 'Mulish',
      fontSize: '1.8rem',
      fontWeight: '700',
    },
  },

  login_button: {
    width: '100%',
    marginTop: '20px',
    background: theme.palette.green,
    color: 'white',
    '&:hover': {
      color: 'black',
    },
  },

  recommendation: {
    fontFamily: 'Mulish',
    fontSize: '.9rem',
  },
  recommendation_link: {
    fontFamily: 'Mulish',
    fontWeight: '600',
    fontSize: '.9rem',
    cursor: 'pointer',
    marginLeft: '5px',
  },
}));

export default function SignInForm({ onclick, setClickData, showToast, path }) {
  const [emailText, setEmailText] = useState('');
  const [passwordText, setPasswordText] = useState('');

  const history = useNavigate();

  const postUser = () => {
    console.log('jssj');
    axios
      .post('http://localhost:8080/login/user-login', {
        userName: emailText,
        password: passwordText,
      })
      .then((res) => console.log(res.json))
      .then(() => history('/'));
  };

  const navigate = useNavigate();
  const dispatch = useDispatch();
  const {
    root_left_lower,
    login_button,
    recommendation,
    recommendation_link,
  } = useStyles();

  return (
    <div className={root_left_lower}>
      <Typography>get your helper easily</Typography>
      <Typography variant="h1" component="h1">
        Login to Your Account
      </Typography>
      <Box marginTop="20px">
        <Formik
          initialValues={{
            loginEmail: '',
            loginPassword: '',
          }}
          onSubmit={async (values) => {
            await dispatch(login(values));

            if (!window.store.getState().authReducer.authenticated) {
              await setClickData({
                type: 'error',
                content: window.store.getState().authReducer.error,
              });
              showToast();
            }
          }}
          validationSchema={Yup.object().shape({
            loginEmail: Yup.string()
              .email('Invalid email format')
              .required('Required'),
            loginPassword: Yup.string()
              .min(6, 'Password must be at least 6 characters')
              .required('Password is required'),
          })}
        >
          {({ isSubmitting }) => (
            <Form autoComplete="off">
              <Grid container>
                <Grid xs={12} item>
                  <Box marginTop="10px">
                    <input
                      type="text"
                      placeholder="Email"
                      onChange={(e) => setEmailText(e.target.value)}
                    />
                  </Box>

                  <Box marginTop="10px">
                    <input
                      type="password"
                      placeholder="Password"
                      onChange={(e) => setPasswordText(e.target.value)}
                    />
                  </Box>

                  <Box marginTop="10px">
                    <button className={login_button} onClick={postUser}>
                      Sign In
                    </button>
                  </Box>
                </Grid>
              </Grid>
            </Form>
          )}
        </Formik>
      </Box>

      <Box alignItems="center" display="flex" marginTop="10px">
        <Typography className={recommendation}>
          Don't have an account?
        </Typography>
        <Typography
          onClick={() => onclick('signup')}
          color="secondary"
          className={recommendation_link}
        >
          Sign Up
        </Typography>
      </Box>
    </div>
  );
}
