import React from 'react';
import { Link } from 'react-router-dom';
import { Typography, makeStyles, Button } from '@material-ui/core';
import ArrowRightAltIcon from '@material-ui/icons/ArrowRightAlt';

const useStyles = makeStyles((theme) => ({
  about_section: {
    display: 'flex',
    width: '100%',
    minHeight: '70vh',
    height: 'max-content',
    // background: "url('foodnapkin.jpg')",
    background: "url('https://thumbs.dreamstime.com/b/people-cleaning-earth-planet-watering-plants-volunteers-taking-care-planet-ecology-environment-nature-protection-158317378.jpg')",
    backgroundRepeat: 'no-repeat',
    objectFit: 'contain',
    backgroundPosition: '-200px',
    backgroundSize: '800px',
    paddingRight: '79px',
    paddingLeft: '118px',
    '@media (max-width: 750px)': {
      padding: '20px 20px',
      backgroundPosition: '-650px',
    },
  },
  about_section_left: {
    width: '50%',
    '@media (max-width: 900px)': {
      width: '30%',
    },
    '@media (max-width: 750px)': {
      width: '0%',
    },
  },
  about_section_right: {
    width: '50%',
    display: 'block',
    padding: '30px 70px',
    '@media (max-width: 900px)': {
      padding: '30px 0px',
      width: '70%',
    },
    '@media (max-width: 750px)': {
      width: '100%',
    },
  },
  about_title: {
    fontWeight: 'bold',
    fontFamily: 'Inter, sans-serif',
    fontSize: '1rem',
    marginBottom: '20px',
  },
  button: {
    borderRadius: '20px',
    width: '150px',
    marginTop: '30px',
    textTransform: 'lowercase',
  },
  leaderBoard_left_h1: {
    lineHeight: '40px',
    fontFamily: 'Inter, sans-serif',
    fontSize: '2.2rem',
    fontWeight: 'bold',
  },
  about_descriptions: {
    fontFamily: 'Inter, sans-serif',
    marginTop: '15px',
  },
}));

export default function AboutSection() {
  const {
    about_section,
    about_section_left,
    about_section_right,
    leaderBoard_left_h1,
    about_title,
    button,
    about_descriptions,
  } = useStyles();
  return (
    <div className={about_section}>
      <div className={about_section_left}></div>
      <div className={about_section_right}>
        <Typography className={about_title} component="h1">
          About
        </Typography>
        <Typography className={leaderBoard_left_h1} variant="h2" component="h1">
          Get help at your doorstep <br /> 
        </Typography>
        <Typography className={about_descriptions} component="p">
        Choose nearest helpers based on their specialization, experience, reviews and rating at fair cost.
        </Typography>
        <Typography className={about_descriptions} component="p">
        For bookings please login or sign up on our website
        </Typography>
        <Button
          disableElevation
          className={button}
          variant="contained"
          color="primary"
          autoCapitalize="none"
          endIcon={<ArrowRightAltIcon />}
          component={Link}
          to={'/allmeals'}
        >
          our category
        </Button>
      </div>
    </div>
  );
}
